eInt(quanty1) + parseInt(cart[i].qty); }    ship = 10; localStorage.setItem('subt', subt); localStorage.setItem('quanty1', quanty1); localStorage.setItem('ship', ship); ttlcharges = parseInt(subt) + parseInt(ship); $('#subt').html('Rs ' + subt); $('#ship').html('Rs ' + ship); $('#ttlcharges').html('Rs ' + ttlcharges); }); function check_pincode(){		var pincode = $("#postal_promo").val(); $.ajax({	      url: '<?php echo site_url('website/check_pincode'); ?>', type: "POST", data: {	          		 'pincode': pincode	             }, success: function (data)	    		   {	                    if (data == '1'){	                    	 $("#veri").css("display", "block"); $("#notveri").css("display", "none"); $("#conshop").attr("disabled", false); } else if (data == '2'){	                    	 $("#veri").css("display", "none"); $("#notveri").css("display", "block"); $("#conshop").attr("disabled", true); if (data == '')                    		 {                				 $("#notveri").css("display", "block"); $("#conshop").attr("disabled", true); }	                    }	                }	           }); }</script>